(36077, '13280', '1', NULL, 1, 'Tạo mới,đăng ngay', '1565', '<p><strong>Với những kinh nghiệm đạt được trong nhiều năm nghi&ecirc;n cứu v&agrave; ph&aacute;t triển tập đo&agrave;n quốc tế Kim Long, c&aacute;c nh&agrave; l&atilde;nh đạo đ&atilde; x&acirc;y dựng l&ecirc;n đội ngũ chuy&ecirc;n gia v&agrave; tư vấn vi&ecirc;n chuy&ecirc;n nghiệp.&nbsp;</strong></p>\r\n\r\n<p>- Tư vấn điều kiện, tr&igrave;nh tự v&agrave; thủ tục đầu tư dự &aacute;n bất động sản theo quy định của cơ quan Nh&agrave; nước c&oacute; thẩm quyền. &nbsp;</p>\r\n\r\n<p>- Tư vấn v&agrave; thực hiện thủ tục chuyển nhượng to&agrave;n bộ hoặc một phần dự &aacute;n bất động sản.</p>\r\n\r\n<p>- Thay mặt Qu&yacute; kh&aacute;ch h&agrave;ng đ&agrave;m ph&aacute;n, thương thảo v&agrave; soạn thảo c&aacute;c hợp đồng li&ecirc;n quan đến bất động sản, như hợp đồng thu&ecirc;, cho thu&ecirc; thương mại, hợp đồng ph&aacute;t triển bất động sản, hợp đồng x&acirc;y dựng.</p>\r\n\r\n<p>- Tư vấn tr&igrave;nh tự để triển khai dự &aacute;n đầu tư x&acirc;y dựng.</p>\r\n\r\n<p>- Tư vấn v&agrave; soạn thảo t&agrave;i liệu cần thiết cho hồ sơ hợp đồng x&acirc;y dựng.</p>', 1544411225, 'Bất động sản và xây dựng', NULL),
(36078, '13281', '1', NULL, 1, 'Tạo mới,đăng ngay', '1566', '<p><strong>Tập đo&agrave;n quốc tế Kim Long c&oacute; một đội ngũ lao động chất lượng cao. Với sự đ&agrave;o tạo từ cơ bản đến n&acirc;ng cao c&ugrave;ng với sự chỉ dạy tận t&igrave;nh của c&aacute;c nh&agrave; chuy&ecirc;n m&ocirc;n Kim Long, đội ngũ lao động đ&atilde; v&agrave; đang c&oacute; sự chuyển biến r&otilde; rệt. Từ những người chưa c&oacute; kinh nghi&ecirc;m,&yacute; thức c&ocirc;ng việc đ&atilde; trờ th&agrave;nh những người lao động :</strong></p>\r\n\r\n<p>- C&oacute; kinh nghiệm chuy&ecirc;n m&ocirc;n từ trung b&igrave;nh đến cao.</p>\r\n\r\n<p>- C&oacute; tr&igrave;nh độ văn h&oacute;a tốt.</p>\r\n\r\n<p>- Được đ&agrave;o tạo ng&ocirc;n ngữ nước ngo&agrave;i để c&oacute; thể giao tiếp với doanh nghiệp quốc tế.</p>\r\n\r\n<p>- C&oacute; khả năng tư duy tốt, l&agrave;m việc độc lập v&agrave; theo nh&oacute;m tốt.</p>\r\n\r\n<p>- Năng động s&aacute;ng tạo trong c&ocirc;ng việc.</p>\r\n\r\n<p>&nbsp;</p>', 1544411267, 'Lao động chất lượng cao', NULL),
(36079, '13282', '1', NULL, 1, 'Tạo mới,đăng ngay', '1567', '<p><strong>Quyền sở hữu tr&iacute; tuệ được xem như một t&agrave;i sản v&agrave; c&ocirc;ng cụ đ&oacute;ng g&oacute;p quan trọng cho th&agrave;nh c&ocirc;ng của mỗi doanh nghiệp. Hiểu r&otilde; tầm quan trọng của Quyền sở hữu tr&iacute; tuệ, Tập đo&agrave;n quốc tế Kim Long đ&atilde; x&acirc;y dựng một bộ phận chuy&ecirc;n tr&aacute;ch tư vấn ph&aacute;p luật trong lĩnh vực Sở hữu tr&iacute; tuệ.</strong>&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i cung ứng c&aacute;c dịch vụ tư vấn như:</p>\r\n\r\n<p>-&nbsp;Tư vấn ph&aacute;p luật v&agrave; đăng k&yacute; bảo hộ nh&atilde;n hiệu.</p>\r\n\r\n<p>-&nbsp;Tư vấn ph&aacute;p luật v&agrave; đăng k&yacute; bảo hộ quyền t&aacute;c gi&aacute; v&agrave; quyền li&ecirc;n quan .</p>\r\n\r\n<p>-&nbsp;Tư vấn ph&aacute;p luật v&agrave; đăng k&yacute; bảo hộ s&aacute;ng chế .</p>\r\n\r\n<p>-&nbsp;Tư vấn ph&aacute;p luật v&agrave; đăng k&yacute; bảo hộ kiểu d&aacute;ng c&ocirc;ng nghiệp.</p>\r\n\r\n<p>- Tư vấn ph&aacute;p luật v&agrave; đăng k&yacute; bảo hộ thiết kế bố tr&iacute; mạch t&iacute;ch hợp.</p>\r\n\r\n<p>-&nbsp;Tư vấn ph&aacute;p luật về chuyển giao quyền sở hữu c&ocirc;ng nghệ.</p>\r\n\r\n<p>-&nbsp;Tư vấn cấp ph&eacute;p hoạt động trang tin điện tử.</p>\r\n\r\n<p>-&nbsp;Tư vấn xử l&yacute; vi phạm trong lĩnh vực sở hữu tr&iacute; tuệ ( Vi phạm, tranh chấp đối với nh&atilde;n hiệu h&agrave;ng h&oacute;a, bản quyền, cạnh tranh &hellip;)</p>', 1544411311, 'Sở hữu trí tuệ', NULL);
