(13260, 'La bàn bất động sản 2018', '1572', 'img_2018-12-08_1544240063.jpeg', 'Với nhiều hậu thuẫn về chính sách, liệu bất động sản năm 2018 có tạo nên sự bùng nổ?\r\nCàng ấn tượng hơn khi trong tổng số 35,88 tỉ USD vốn FDI thu hút được trong năm 2017, có tới 3,05 tỉ USD thuộc về bất động sản, đưa lĩnh vực này đứng thứ ba trong số các ngôi sao thu hút được nhiều vốn nước ngoài nhất. Theo số liệu của Bộ Xây dựng, trong năm 2017, lượng giao dịch bất động sản tăng mạnh so với năm 2016.', NULL, '1', 1, 1544240063, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543997715, 1544240063, 'la-ban-bat-dong-san-2018', 1543997640, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
(13261, 'Giảm giá nhưng không giảm lợi nhuận?', '1570', 'img_2018-12-08_1544240080.jpeg', 'Giáo sư Marketing của trường Kinh doanh Harvard nói: \"Một trong những đòn bẩy quan trọng nhất của các nhà bán lẻ mang lại cho khách hàng chính là giảm giá\".\r\nCác nhà bán lẻ từ lâu đã biết cách đặt các gian hàng giảm giá ở phía sau của cửa hàng, nơi khách hàng phải mất công đi lại mới tìm ra nó. Bằng cách đó, họ có thể bị cám dỗ khi đi qua các gian hàng khác và sẽ mua một vài mặt hàng nguyên giá, giúp tăng lợi nhuận của cửa hàng.', NULL, '1', 1, 1544240080, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543997769, 1544240080, 'giam-gia-nhung-khong-giam-loi-nhuan', 1543997700, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
(13262, 'Con nợ thời 4.0', '1571', 'img_2018-12-05_1543998466.jpeg', 'Các công ty fintech muốn dựa vào công nghệ và dữ liệu để nhảy vào chiếm lĩnh thị trường vay tiêu dùng đang phát triển mạnh.', NULL, '1', 1, 1543998466, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543998466, 1543998466, 'con-no-thoi-40', 1543998360, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
(13263, 'KHÁCH SẠN', '1558', 'img_2018-12-05_1543998725.jpeg', 'Khách sạn 5 sao Hồng Vận ngoài vẻ đẹp về kiến trúc và vị trí địa lý đắc địa, chúng tôi được cấp phép cung cấp dịch vụ vui chơi giải trí casino bên trong khách sạn. Tại đó, khách du lịch có thể chiêm ngưỡng vẻ đẹp công trình kiến trúc của khách sạn, sử dụng các dịch vụ tiện ích, thư giãn với các dịch vụ chăm sóc sức khỏe chất lượng cao.', NULL, '1', 1, 1543999742, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543998724, 1543999742, 'khach-san', 1543998540, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
(13264, 'Bất Động Sản', '1559', 'img_2018-12-05_1543999820.jpeg', 'Nằm sát cạnh khu thương mại mậu dịch tự do vùng cửa khẩu, với diện tích 70 hecta, dự án khu đô thị mới dọc tuyến đường biên giới sông Bắc Luân được Công ty CP Quốc tế Kim Long làm chủ đầu tư... Quy hoạch chi tiết xây dựng khu dân cư đô thị mới dọc tuyến đường biên giới sông Bắc Luân, Thành phố Móng Cái, tỉnh Quảng Ninh.', NULL, '1', 1, 1543999820, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543999820, 1543999819, 'bat-dong-san', 1543999740, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
(13265, 'Kinh doanh Trầm Hương', '1560', 'img_2018-12-05_1543999869.jpeg', 'Công ty sản xuất và buôn bán các sản phẩm từ trầm hương mang nhãn hiệu Trầm Hương Kim Long. Đến với Kim Long, quý khách sẽ có thể lựa chọn cho mình những sản phẩm vô cùng chất lượng.', NULL, '1', 1, 1543999868, 1, 0, 0, NULL, NULL, 0, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, NULL, '', NULL, 0, 1543999868, 1543999868, 'kinh-doanh-tram-huong', 1543999800, 0, 0, 0, NULL, NULL, NULL, NULL, 0, 0, 0, 0),
